import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import QRCode from "qrcode";

function InstructorAttendance() {
  const navigate = useNavigate();
  const [text, setText] = useState("");
  const qrRef = useRef();
  const inputRef = useRef();

  useEffect(() => {
    // Initialize QRCode object when component mounts
    const qrcode = new QRCode(qrRef.current);

    // Function to generate the QR code
    const makeCode = () => {
      if (!text) {
        alert("Input a text");
        inputRef.current.focus();
        return;
      }
      qrcode.makeCode(text);
    };

    // Generate the QR code whenever the text state changes
    makeCode();

    // Clean up function to remove the QRCode instance
    return () => {
      qrcode.clear(); // Clear the QR code when component unmounts
    };
  }, [text]);

  const handleInputChange = (event) => {
    setText(event.target.value);
  };

  const handleKeyDown = (event) => {
    if (event.keyCode === 13) {
      setText(event.target.value);
    }
  };

  const handleBlur = () => {
    setText(inputRef.current.value);
  };

  return (
    <div className="InstructorAttendance">
      <div id="qrcode" ref={qrRef}></div>
      <input
        id="text"
        type="text"
        ref={inputRef}
        value={text}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown}
        onBlur={handleBlur}
      />
      <button
        className="InstructorAttendance_Done"
        onClick={() => navigate("/InstructorHome")}
      >
        Cancel
      </button>
    </div>
  );
}

export default InstructorAttendance;
