import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import QRCode from "qrcode";

function InstructorAttendance() {
  const navigate = useNavigate();
  const [text, setText] = useState("Hello, World!");
  const qrRef = useRef();

  useEffect(() => {
    if (qrRef.current && text) {
      const canvas = qrRef.current;
      QRCode.toCanvas(
        canvas,
        text,
        {
          colorDark: "#000000",
          colorLight: "#ffffff",
          correctLevel: QRCode.CorrectLevel || 1, // Default to 1 if CorrectLevel is undefined
        },
        (error) => {
          if (error) console.error(error);
        }
      );
    }
  }, [text]);

  return (
    <div className="InstructorAttendance">
      <canvas id="qrcode" ref={qrRef}></canvas>
      <div className="button-container">
        <button
          className="InstructorAttendance_Done"
          onClick={() => navigate("/InstructorHome")}
        >
          Cancel
        </button>
        <button
          className="InstructorAttendance_Finish"
          onClick={() => navigate("/InstructorDone")}
        >
          Finish
        </button>
      </div>
    </div>
  );
}

export default InstructorAttendance;

