import React, { useRef, useState } from "react";
import Webcam from "react-webcam";
import { Button } from "@mui/material";
import jsQR from "jsqr";
import "./App.css"; // Make sure to import the CSS file

function StudentHome() {
  const webcamRef = useRef(null);
  const [img, setImg] = useState(null);
  const [qrCodeText, setQrCodeText] = useState("");

  const capture = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImg(imageSrc);

    if (imageSrc) {
      // Decode the image to find a QR code
      const code = decodeQRFromImage(imageSrc);
      if (code) {
        setQrCodeText(code.data);
      } else {
        setQrCodeText("No QR code detected.");
      }
    }
  };

  const decodeQRFromImage = (imageSrc) => {
    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");
    const image = new Image();
    image.src = imageSrc;
    image.onload = () => {
      canvas.width = image.width;
      canvas.height = image.height;
      ctx.drawImage(image, 0, 0, canvas.width, canvas.height);
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      const code = jsQR(imageData.data, imageData.width, imageData.height, {
        inversionAttempts: "dontInvert",
      });
      return code;
    };
  };

  return (
    <div style={{ position: "relative" }}>
      <Webcam
        className="webcam"
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
      />
      <Button variant="contained" onClick={capture}>
        I'm Here
      </Button>
      {img && <img src={img} alt="Webcam Capture" />}
      {qrCodeText && <p>{qrCodeText}</p>}
    </div>
  );
}

export default StudentHome;
